CREATE TABLE hits(ip VARCHAR(20) NOT NULL,self_ip VARCHAR(20) NOT NULL,year INTEGER DEFAULT -1,month INTEGER DEFAULT -1,day INTEGER DEFAULT -1,hour INTEGER DEFAULT -1,minute INTEGER DEFAULT -1,second INTEGER DEFAULT -1,t_hash VARCHAR(70) NOT NULL,p_hash VARCHAR(70) NOT NULL,b_hash VARCHAR(70) NOT NULL,guid VARCHAR(50),client VARCHAR(30),port INTERGER DEFAULT 0,self_port INTEGER DEFAULT 0,filename VARCHAR(255),country VARCHAR(20),project VARCHAR(30),isp VARCHAR(50),city VARCHAR(50),time VARCHAR(50),region VARCHAR(100),filesize INTEGER DEFAULT 0,disconnect_time VARCHAR(50) DEFAULT NULL, block_size INTEGER DEFAULT 0,piece_size INTEGER DEFAULT 0,block INTEGER DEFAULT 0,piece INTEGER DEFAULT 0, path VARCHAR(255) DEFAULT NULL, sig VARCHAR(50) DEFAULT NULL, CONSTRAINT [ip_time_guid] UNIQUE([ip], [month], [day], [hour], [guid], [piece], [block]) ON CONFLICT REPLACE);